<?php

namespace Ivoz\Provider\Infrastructure\Persistence\Doctrine;

use Doctrine\Bundle\DoctrineBundle\Repository\ServiceEntityRepository;
use Ivoz\Provider\Domain\Model\HuntGroupMember\HuntGroupMember;
use Ivoz\Provider\Domain\Model\HuntGroupMember\HuntGroupMemberRepository;
use Doctrine\Persistence\ManagerRegistry;

/**
* HuntGroupMemberDoctrineRepository*
* This class was generated by the Doctrine ORM. Add your own custom
* repository methods below.
*
* @template-extends ServiceEntityRepository<HuntGroupMember>
*/
class HuntGroupMemberDoctrineRepository extends ServiceEntityRepository implements HuntGroupMemberRepository
{
    public function __construct(ManagerRegistry $registry)
    {
        parent::__construct($registry, HuntGroupMember::class);
    }

    public function findUserIdsInHuntGroup(
        int $huntGroupId
    ): array {
        $qb = $this
            ->createQueryBuilder('self');

        $expression = $qb->expr();

        $qb
            ->select('IDENTITY(self.user) as user')
            ->where(
                $expression->eq('self.huntGroup', $huntGroupId)
            )
            ->andWhere(
                $expression->isNotNull('self.user')
            );

        $result = $qb->getQuery()->getScalarResult();

        return array_map(
            function ($row) {
                return (int) $row['user'];
            },
            $result
        );
    }
}
